 assignedUsers[getCurrentUserId()] = "Manager"

 val board = Board(
 etBoardName.text.toString(),
 mBoardImageURL,
 mUserName,
 assignedUsers,
 "",
 ArrayList(),
 switchPublicProject.isChecked
 )

 FirestoreClass().createBoard(this,board)

 }

